Script.script:
function downloadPkg(a,b) echo("****Downloading package.") downloadFile(getMudletHomeDir() .. "/CFGUI.zip", "https://github.com/Rahsael/CFGUI/releases/latest/download/CFGUI.zip") end
function installReady(event, file) if file ~= getMudletHomeDir().."/CFGUI.zip" then return end if not io.exists(getMudletHomeDir().."/CFGUI.zip") then cecho([[<gold>To download the Carrion Fields Mudlet client, please copy and paste the following code into the command line:
lua function d(a,b)if not b:find("CFGUI",1,true)then return end installPackage(b)os.remove(b)cecho("<lime_green>Package installed!\n")end registerAnonymousEventHandler("sysDownloadDone","d")downloadFile(getMudletHomeDir().."/CFGUI.zip","https://github.com/Rahsael/CFGUI/releases/latest/download/CFGUI.zip")
]]) return end
cecho([[
<maroon>*<gold> Your Carrion Fields client was successfully downloaded.
]]) cecho("\r\n<maroon>*<gold> Installing... <gray>(this will take a few seconds.)\r\n") tempTimer(2, [[installPackage(getMudletHomeDir().."/CFGUI.zip")]]) uninstallPackage("CF_Loader") end
registerAnonymousEventHandler("sysConnectionEvent", "downloadPkg") registerAnonymousEventHandler("sysDownloadDone", "installReady")
