Alias.script:
local room = matches[3] or mmp.currentroom room = tonumber(room) or mmp.relativeroom(mmp.currentroom, room) if not room or not mmp.roomexists(room) then mmp.echo("Sorry - which room do you want to put this character in? I don't know where you are at the moment, if you want to do the current room.") return end
local char = matches[2]
if char == "clear" then setRoomChar(room, ' ') mmp.echo("Cleared the character from "..room.." ("..getRoomName(room)..")") else setRoomChar(room, char) mmp.echo("Set the "..char:sub(1,1).." character on "..room.." ("..getRoomName(room)..")") end centerview(mmp.currentroom)

local continents = mmp.getcontinents()
if not next(continents) then mmp.echo("No continents known.") else for continent, areadata in pairs(continents) do mmp.echo(continent.." continent:")
for _, areaid in ipairs(areadata) do cecho(" "..getRoomAreaName(areaid).."\n") end end end

if (matches[2] and matches[2] == "on") or (not matches[2] and not mmp.editing) then mmp.editing = true enableAlias("mm Mapping")
mmp_regenerate_areas() mmp.highlight_unfinished_rooms()
mmp.echo("Mapping mode enabled. Happy mapping!") elseif (matches[2] and matches[2] == "off") or (not matches[2] and mmp.editing) then mmp.editing = false disableAlias("mm Mapping") mmp.echo("Mapping mode disabled.") end

mmp = mmp or {}
mmp.getPathPerf = mmp.getPathPerf or createStopWatch() startStopWatch(mmp.getPathPerf)
local from, to = tonumber(matches[2]), tonumber(matches[3])
getPath(from, to)
mmp.echon = mmp.echon or echo mmp.echon("a new getPath() from "..from.." to "..to.." took "..stopStopWatch(mmp.getPathPerf).."s. There are "..#speedWalkPath.." rooms to visit in it.") echo(" ") echoLink("[unhighlight]", [[ for room in pairs(mmp.getpathhighlights) do unHighlightRoom(room) end ]], "Click me to remove highlighting from getpath")
mmp.getpathhighlights = mmp.getpathhighlights or {}
for room in pairs(mmp.getpathhighlights) do unHighlightRoom(room) end
mmp.getpathhighlights = {}
local r,g,b = unpack(color_table.yellow) local br,bg,bb = unpack(color_table.yellow) -- add the first room to the speedWalkPath, as we'd like it highlighted as well table.insert(speedWalkPath, 1, from) for i = 1, #speedWalkPath do local room = speedWalkPath[i] highlightRoom(room, r,g,b,br,bg,bb, 1, 255, 255) mmp.getpathhighlights[room] = true end
centerview(from)

Script.script:
--[[
mmp will have 2 person tracking databases:
- mmp.pdb  =  used to store the last known location of a person
- mmp.pdb_lastupdate  =  used to store the output of the last locating command

Both databases work with the person's name as key and the location name as value.

]]
mmp.pdb = mmp.pdb or {}
mmp.pdb_lastupdate = mmp.pdb_lastupdate or {}

function mmp.is_here(who)
  return (mmp.pdb[who] and mmp.pdb[who] == mmp.currentroomname) and true or false
end

function mmp_save_options()
	local saveTable = {
		locked_areas = mmp.locked,
		options = mmp.settings:getAllOptions()
	}

	if string.char(getMudletHomeDir():byte()) == "/" then _sep = "/" else  _sep = "\\" end
	local saveFile = getMudletHomeDir() ..  _sep .. "mapper.options.lua"

	table.save(saveFile, saveTable)
end

